import React from "react";


export default function Paginado ({ countriesPage, allCountries, paginado }) {
    const pageNumbers = [];

    for (let i=0; i<=Math.ceil(allCountries/countriesPage); i++){  // redondea las countri sobre la cantidad que se quiere por pagina 
       pageNumbers.push(i)
    }

    return (
        <nav>
            <ul>
                {pageNumbers && pageNumbers.map(number =>(
                    <li>
                    <a onClick={()=> paginado(number)}>{number}</a>
                    </li>
                ))}
            </ul>
        </nav>

    )


}
