import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { GetDetail } from "../actions";
import { useEffect } from "react";

export default function Detail(props) {
const dispatch = useDispatch()
const detail = useSelector(state => state.detail)
const countrys = useSelector((state) => state.detail)
useEffect(()=> {
dispatch(GetDetail(props.match.params.id));
},[dispatch])

const activities = detail.activities?.map(e => {
    return {
        name: e.name,
        difficulty: e.difficulty,
        duration: e.duration,
        season: e.season
    }
})





return(
<div>
<div>
    {
        !countrys?.length > 0 ? //no es verdadero que la longitud de countrys sea mayor que 0

        //Si el resultado es falso, se ejecutará 
        //               |
        //               V
        <div>
               <h1>{countrys.name}</h1>
               <img src={countrys.image}/>
               <p>{countrys.continent}</p>
               <p>{countrys.capital}</p>
               <p>{countrys.subregion}</p>
               <p>{countrys.area}</p>
               <p>{countrys.population}</p>
             </div>
//Si el resultado de la evaluación es verdadero, se ejecutará la primera parte de la expresión:
             :  <p>LOADING....</p>
             }
              
</div>

<div>
{activities?.length > 0 ? activities?.map(e => {
                                        return (
                                            <div key={e.id}>
                                                <p>Name: {e.name}</p>
                                                <p>Difficulty: {e.difficulty}</p>
                                                <p>Duration: {e.duration}</p>
                                                <p>Season: {e.season}</p>
                                                <hr></hr>
                                            </div>
                                        )
                                    })
                                        : <p>Without activities</p>}

</div>

<Link to = "/Home"><button>volver</button></Link> 
</div>

)



}
