const initialState ={
    countries: [],
    allContinents: []
}

function rootReducer (state= initialState, action){
    switch(action.type){
        case "GET_COUNTRIES":
            return{
                ...state,
                countries: action.payload
            }
            case "FILTER_CONTINENT":
            // const allCountries = state.countries
            // const statusFil = action.payload === "All" ? allCountries: allCountries.filter(e => e.continent === action.payload)
               
            const allContinents = state.allContinents
            const statusFil = action.payload === "All" ? allContinents:
             allContinents.filter(e => e.continent === action.payload)
                
            
            return {
                    ...state,
                    countries: statusFil

                }
            default:
                return state;
    }
}


export default rootReducer;
