const initialState ={
    countries: [],
    allContinents: [],
    allPopulation: []
}

function rootReducer (state= initialState, action){
    switch(action.type){
        case "GET_COUNTRIES":
            return{
                ...state,
                countries: action.payload,
                allContinents: action.payload,
                allPopulation: action.payload
            }
            case "FILTER_CONTINENT":
            const allContinents = state.allContinents
            const statusFil = action.payload === "All" ? allContinents:
            allContinents.filter(e => e.continent === action.payload)    
            return {
                    ...state,
                    countries: statusFil
                }

             case "FILTER_POPULATION" :
            //const allPopulation = state.allPopulation
            const statusPo = action.payload === 'Min' ?
            
            
           // action.payload === 'Min' ?
                state.countries.sort(function (a, b) {
                    if (a.allPopulation > b.allPopulation) {
                        return 1;
                    }
                    if (b.allPopulation > a.allPopulation) {
                        return -1;
                    }
                    return 0;
                }) :
                state.countries.sort(function (a, b) {
                    if (a.allPopulation > b.allPopulation) {
                        return -1;
                    }
                    if (b.allPopulation > a.allPopulation) {
                        return 1;
                    }
                    return 0;
                })
            return {
                ...state,
                allPopulation: statusPo
            }
// "FILTER_POPULATION"  

                
            default:
                return state;
    }
}


export default rootReducer;
